INSERT INTO `thongtin` VALUES (1,'<div class=\"dark-grey-text mb-lg-0 mb-md-5 mb-4\" id=\"postinfo_277_content\"><p style=\"margin-top:25px\"><span style=\"color:#e74c3c;\"><span style=\"font-size:24px;\"><span style=\"background:white\"><span style=\"vertical-align:baseline\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"border: 1pt none windowtext; padding: 0in;\"><span style=\"font-family:&quot;inherit&quot;,serif\">GIỚI THIỆU CHUNG</span></span></span></span></span></span></span></span></p>\n\n<p style=\"text-align: justify;\"><span style=\"font-size:18px;\"><span style=\"background:white\"><span style=\"line-height:normal\"><span style=\"vertical-align:baseline\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Được thành lập vào năm 1990 và trở thành trường đại học công lập từ năm 2006, đến nay Trường Đại học Mở Thành phố Hồ Chí Minh là trường đại học đa ngành trực thuộc Bộ Giáo dục và Đào tạo, có nhiệm vụ đào tạo đại học và sau đại học, với các hình thức đào tạo chính quy và giáo dục thường xuyên, đào tạo các điểm vệ tinh,…nhằm đáp ứng nhu cầu học tập đa dạng của xã hội, góp phần tăng cường đội ngũ cán bộ khoa học-kỹ thuật cho đất nước.</span></span></span></span></span></span></span></p>\n\n<table align=\"center\" class=\"MsoTableGrid\" style=\"width:581.5pt; border-collapse:collapse; border:undefined\" width=\"775\">\n	<tbody>\n		<tr>\n			<td style=\"width:69.35pt; padding:0cm 2.85pt 0cm 2.85pt\" width=\"92\">\n			<p align=\"center\" style=\"margin-left:-4px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"vertical-align:baseline\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\"><img height=\"80\" src=\"https://tuyensinh.ou.edu.vn/media/photos/image-2024